import "./Login.css";
import Card from "../UI/Card";
import React, { useState, useEffect } from "react";
import ErrorModal from "../ErrorModule/ErrorModal";

function Login(props) {
  const loginDetails = [
    { email: "@", pwd: "demo12345" },
    { email: "nave@", pwd: "nave2222" },
    { email: "lava@", pwd: "demlava22222o12345" },
  ];
  const [enteredEmail, setEmailValue] = useState("");
  const [enteredPassword, setPassword] = useState("");
  const [emailValid, setEmailValidator] = useState();
  const [passwordValid, setPasswordValid] = useState();
  const [errorHandler, SetErrorHandler] = useState(false);
  const [formValid, setFormValid] = useState(false);
  useEffect(() => {
    setTimeout(() => {
      console.log("validating");
      setFormValid(
        enteredEmail.includes("@") && enteredPassword.trim().length > 6
      );
    }, 500);
  }, [enteredEmail, enteredPassword]);
  function emailChangeHandler(event) {
    setEmailValue(event.target.value);
  }
  function passwordChangeHandler(event) {
    setPassword(event.target.value);
    setFormValid(
      event.target.value.trim().length > 6 && enteredEmail.includes("@")
    );
  }
  function emailValidator() {
    setEmailValidator(enteredEmail.includes("@"));
  }
  function passwordValidator() {
    setPasswordValid(enteredPassword.trim().length > 6);
  }
  function submitHandler(event) {
    event.preventDefault();
    const email = loginDetails.find((obj) => obj.email === enteredEmail);
    if (email) {
      const d = loginDetails.find((ob) => email.pwd === enteredPassword);
      if (d) {
        props.loginHandler(enteredEmail, enteredPassword);
      } else {
        SetErrorHandler({
          title: "Invalid Password",
          message: "Entered Valid Password",
        });
        return;
      }
      props.loginHandler(enteredEmail, enteredPassword);
    } else {
      SetErrorHandler({
        title: "Invalid input",
        message: "Entered Email and password are not registered",
      });
      return;
    }
  }
  function errorRemover() {
    SetErrorHandler(null);
  }
  return (
    <Card>
      {errorHandler && (
        <ErrorModal
          message={errorHandler.title}
          description={errorHandler.message}
          errorRemover={errorRemover}
        />
      )}
      <form onSubmit={submitHandler}>
        <div className="email">
          <label>E-mail</label>
          <input
            className={emailValid === false ? "emailInvalid" : ""}
            onChange={emailChangeHandler}
            onBlur={emailValidator}
          ></input>
        </div>
        <div className="password">
          <label>Password</label>
          <input
            onChange={passwordChangeHandler}
            onBlur={passwordValidator}
            className={passwordValid === false ? "pwdInvalid" : ""}
          ></input>
        </div>
        <div className="btn">
          <button
            className={!formValid ? "invalid" : " valid"}
            disabled={!formValid}
          >
            Login
          </button>
        </div>
      </form>
    </Card>
  );
}
export default Login;
